INSERT INTO `feedback` (`Id`, `title_vn`, `title_en`, `description_vn`, `description_en`, `content_vn`, `content_en`, `images`, `sort`, `alias`, `ticlock`) VALUES
(1, 'Bảng Giá Tham Khảo', 'General Hospital Charges', 'Gồm:\r\n Giá khám, Giá một số xét nghiệm thông thường như xét nghiệm máu, chụp X-Quang phổi thẳng, siêu âm bụng, siêu âm tuyến vú, pap mear.', 'Gồm:\r\n Giá khám, Giá một số xét nghiệm thông thường như xét nghiệm máu, chụp X-Quang phổi thẳng, siêu âm bụng, siêu âm tuyến vú, pap mear.', '<p style="box-sizing: border-box; margin: 0px; color: rgb(96, 96, 96); font-family: arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 18px; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; background-color: rgb(255, 255, 255);"><span style="font-size: 12px;"><span style="font-family: Arial;"><strong style="box-sizing: border-box; font-weight: 700;">Tư Vấn &amp; Chăm S&oacute;c Kh&aacute;ch H&agrave;ng</strong></span></span></p>\r\n<p style="box-sizing: border-box; margin: 0px; color: rgb(96, 96, 96); font-family: arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 18px; orphans: auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-align: justify; background-color: rgb(255, 255, 255);"><span style="font-size: 12px;"><span style="font-family: Arial;">Bộ phận Chăm s&oacute;c Kh&aacute;ch h&agrave;ng của<span class="Apple-converted-space">&nbsp;</span></span></span><span style="font-size: 12px;"><a style="box-sizing: border-box; color: rgb(66, 139, 202); text-decoration: none; background: 0px 0px;" href="http://vuanhhospital.com.vn/detal/benh-vien-da-khoa-quoc-te-vu-anh-dcwdipdnfi"><span style="font-family: Arial;"><strong style="box-sizing: border-box; font-weight: 700;">Bệnh Viện Quốc Tế</strong></span></a><span style="font-family: Arial;"><span class="Apple-converted-space">&nbsp;</span>Vũ Anh hoạt động với ti&ecirc;u ch&iacute; đem lại sự thoải m&aacute;i, h&agrave;i l&ograve;ng nhất đến cho bệnh nh&acirc;n đến thăm kh&aacute;m v&agrave; điều trị nội tr&uacute; tại đ&acirc;y. Ch&uacute;ng t&ocirc;i sẵn s&agrave;ng tiếp nhận c&aacute;c &yacute; kiến đ&oacute;ng g&oacute;p, thắc&nbsp;mắc, y&ecirc;u cầu của qu&yacute; bệnh nh&acirc;n với mục đ&iacute;ch ng&agrave;y c&agrave;ng cải thiện chất lượng chuy&ecirc;n m&ocirc;n v&agrave; dịch vụ của Bệnh Viện. Đến với bộ phận Chăm S&oacute;c Kh&aacute;ch H&agrave;ng, qu&yacute; bệnh nh&acirc;n v&agrave; kh&aacute;ch h&agrave;ng sẽ nhận được sự tư vấn tận t&igrave;nh về c&aacute;c thủ tục, chi ph&iacute; kh&aacute;m bệnh v&agrave; điều trị; bổ sung c&aacute;c thủ tục giấy tờ, giải đ&aacute;p thắc mắc trực tiếp, qua điện thoại hoặc li&ecirc;n lạc bằng email. Ph&ograve;ng cũng lu&ocirc;n thăm hỏi t&igrave;nh h&igrave;nh bệnh nh&acirc;n trong v&agrave; sau qu&aacute; tr&igrave;nh điều trị v.v&hellip;</span></span></p>', '<p style="box-sizing: border-box; margin: 0px; color: rgb(96, 96, 96); font-family: arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 18px; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; background-color: rgb(255, 255, 255);"><span style="font-size: 12px;"><span style="font-family: Arial;"><strong style="box-sizing: border-box; font-weight: 700;">Tư Vấn &amp; Chăm S&oacute;c Kh&aacute;ch H&agrave;ng</strong></span></span></p>\r\n<p style="box-sizing: border-box; margin: 0px; color: rgb(96, 96, 96); font-family: arial; font-size: 13px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 18px; orphans: auto; text-indent: 0px; text-transform: none; white-space: normal; widows: 1; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-align: justify; background-color: rgb(255, 255, 255);"><span style="font-size: 12px;"><span style="font-family: Arial;">Bộ phận Chăm s&oacute;c Kh&aacute;ch h&agrave;ng của<span class="Apple-converted-space">&nbsp;</span></span></span><span style="font-size: 12px;"><a style="box-sizing: border-box; color: rgb(66, 139, 202); text-decoration: none; background: 0px 0px;" href="http://vuanhhospital.com.vn/detal/benh-vien-da-khoa-quoc-te-vu-anh-dcwdipdnfi"><span style="font-family: Arial;"><strong style="box-sizing: border-box; font-weight: 700;">Bệnh Viện Quốc Tế</strong></span></a><span style="font-family: Arial;"><span class="Apple-converted-space">&nbsp;</span>Vũ  Anh hoạt động với ti&ecirc;u ch&iacute; đem lại sự thoải m&aacute;i, h&agrave;i l&ograve;ng nhất đến cho  bệnh nh&acirc;n đến thăm kh&aacute;m v&agrave; điều trị nội tr&uacute; tại đ&acirc;y. Ch&uacute;ng t&ocirc;i sẵn s&agrave;ng  tiếp nhận c&aacute;c &yacute; kiến đ&oacute;ng g&oacute;p, thắc&nbsp;mắc, y&ecirc;u cầu của qu&yacute; bệnh nh&acirc;n với  mục đ&iacute;ch ng&agrave;y c&agrave;ng cải thiện chất lượng chuy&ecirc;n m&ocirc;n v&agrave; dịch vụ của Bệnh  Viện. Đến với bộ phận Chăm S&oacute;c Kh&aacute;ch H&agrave;ng, qu&yacute; bệnh nh&acirc;n v&agrave; kh&aacute;ch h&agrave;ng  sẽ nhận được sự tư vấn tận t&igrave;nh về c&aacute;c thủ tục, chi ph&iacute; kh&aacute;m bệnh v&agrave;  điều trị; bổ sung c&aacute;c thủ tục giấy tờ, giải đ&aacute;p thắc mắc trực tiếp, qua  điện thoại hoặc li&ecirc;n lạc bằng email. Ph&ograve;ng cũng lu&ocirc;n thăm hỏi t&igrave;nh h&igrave;nh  bệnh nh&acirc;n trong v&agrave; sau qu&aacute; tr&igrave;nh điều trị v.v&hellip;</span></span></p>', 'bang-gia-tham-khao-1461121381.jpg', 1, 'bang-gia-tham-khao', '0'),
(2, 'Bảng Giá Dịch Vụ Trọn Gói', 'Healthcare Package Price', 'Gói sinh con, Gói khám sức khỏe tổng quát', 'Delivery Package Price, General Health Check-up Package Price', '<p>Đang cập nhật</p>', '<p>Đang cập nhật</p>', 'bang-gia-dich-vu-tron-goi-1461121449.jpg', 2, 'bang-gia-dich-vu-tron-goi', '0'),
(3, 'Bảng Giá Phòng Bệnh Nội Trú', 'Inpatient Room Charges', 'Khi sử dụng một trong những phương tiện liên lạc trên, khách hàng đảm bảo sẽ nhận được sự chăm sóc', 'Khi sử dụng một trong những phương tiện liên lạc trên, khách hàng đảm bảo sẽ nhận được sự chăm sóc', '<p style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;">Tọa lạc trong một khu&ocirc;n vi&ecirc;n xanh m&aacute;t, kh&ocirc;ng kh&iacute; trong l&agrave;nh, kh&ocirc;ng gian được thiết kế hiện đại, tho&aacute;ng rộng, bệnh viện Vũ Anh lu&ocirc;n mang đến cảm gi&aacute;c thoải m&aacute;i v&agrave; thư gi&atilde;n cho bệnh nh&acirc;n khi đến thăm v&agrave; điều trị.<br />\r\n<br />\r\nPh&ograve;ng bệnh được thiết kế theo hướng kh&ocirc;ng gian mở, tận dụng tối đa &aacute;nh s&aacute;ng tự nhi&ecirc;n gi&uacute;p cho người bệnh c&oacute; được sự thư gi&atilde;n tối đa. Nội thất được thiết kế trang nh&atilde;, bố tr&iacute; thuận tiện cho bệnh nh&acirc;n nghỉ dưỡng v&agrave; chữa bệnh. Tất cả c&aacute;c ph&ograve;ng ri&ecirc;ng tại bệnh viện Vũ Anh c&ograve;n được trang bị c&aacute;c tiện nghi hiện đại: điện thoại, truyền h&igrave;nh c&aacute;p, đường truyền internet kh&ocirc;ng d&acirc;y tốc độ cao phục vụ giải tr&iacute; cũng như li&ecirc;n lạc c&aacute; nh&acirc;n, hệ thống camera thăm bệnh từ xa, giải quyết c&ocirc;ng việc cho bệnh nh&acirc;n hoặc cho c&aacute;c doanh nh&acirc;n trong thời gian kh&aacute;m, điều trị tại đ&acirc;y.<br />\r\n<br />\r\n<strong>PH&Ograve;NG ĐƠN II&nbsp; </strong><br />\r\n<em>Gi&aacute; ph&ograve;ng l&agrave; 2.600.000 đồng <strong>&nbsp;</strong></em><br />\r\n</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;"><br />\r\n</span></span></p>\r\n<table cellspacing="1" cellpadding="1" border="0" style="width:700px">\r\n    <tbody>\r\n        <tr>\r\n            <td style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;"><img style="height:267px; width:400px" alt="" src="http://vuanhhospital.com.vn/Upload/images/IMG_phong%20benh.jpg" /></span></span></td>\r\n            <td style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;"><img style="height:267px; width:400px" alt="" src="http://vuanhhospital.com.vn/Upload/images/IMG_phong%20benh%2001%282%29.jpg" /></span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;">&nbsp;</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;"><strong>PH&Ograve;NG ĐƠN I</strong><br />\r\n<em>Gi&aacute; ph&ograve;ng l&agrave; 1.900.000 đồng</em><br />\r\n</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;"><strong>PH&Ograve;NG HAI GIƯỜNG</strong><br />\r\n<em>Gi&aacute; ph&ograve;ng l&agrave; 1.000.000 đồng/1 giường</em><br />\r\nVấn đề dinh dưỡng cũng l&agrave; yếu tố rất được quan t&acirc;m tại Vũ Anh. C&aacute;c đầu bếp, điều dưỡng vi&ecirc;n sẽ phục vụ bệnh nh&acirc;n, th&acirc;n nh&acirc;n 24/24 giờ theo thực đơn cho từng trường hợp bệnh l&yacute; v&agrave; sở th&iacute;ch của bệnh nh&acirc;n. Bữa ăn được nh&acirc;n vi&ecirc;n phục vụ tận ph&ograve;ng bệnh nh&acirc;n.</span></span></p>\r\n<p>&nbsp;</p>', '<p style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;">Tọa  lạc trong một khu&ocirc;n vi&ecirc;n xanh m&aacute;t, kh&ocirc;ng kh&iacute; trong l&agrave;nh, kh&ocirc;ng gian  được thiết kế hiện đại, tho&aacute;ng rộng, bệnh viện Vũ Anh lu&ocirc;n mang đến cảm  gi&aacute;c thoải m&aacute;i v&agrave; thư gi&atilde;n cho bệnh nh&acirc;n khi đến thăm v&agrave; điều trị.<br />\r\n<br />\r\nPh&ograve;ng  bệnh được thiết kế theo hướng kh&ocirc;ng gian mở, tận dụng tối đa &aacute;nh s&aacute;ng  tự nhi&ecirc;n gi&uacute;p cho người bệnh c&oacute; được sự thư gi&atilde;n tối đa. Nội thất được  thiết kế trang nh&atilde;, bố tr&iacute; thuận tiện cho bệnh nh&acirc;n nghỉ dưỡng v&agrave; chữa  bệnh. Tất cả c&aacute;c ph&ograve;ng ri&ecirc;ng tại bệnh viện Vũ Anh c&ograve;n được trang bị c&aacute;c  tiện nghi hiện đại: điện thoại, truyền h&igrave;nh c&aacute;p, đường truyền internet  kh&ocirc;ng d&acirc;y tốc độ cao phục vụ giải tr&iacute; cũng như li&ecirc;n lạc c&aacute; nh&acirc;n, hệ  thống camera thăm bệnh từ xa, giải quyết c&ocirc;ng việc cho bệnh nh&acirc;n hoặc  cho c&aacute;c doanh nh&acirc;n trong thời gian kh&aacute;m, điều trị tại đ&acirc;y.<br />\r\n<br />\r\n<strong>PH&Ograve;NG ĐƠN II&nbsp; </strong><br />\r\n<em>Gi&aacute; ph&ograve;ng l&agrave; 2.600.000 đồng <strong>&nbsp;</strong></em><br />\r\n</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;"><br />\r\n</span></span></p>\r\n<table cellspacing="1" cellpadding="1" border="0" style="width:700px">\r\n    <tbody>\r\n        <tr>\r\n            <td style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;"><img style="height:267px; width:400px" alt="" src="http://vuanhhospital.com.vn/Upload/images/IMG_phong%20benh.jpg" /></span></span></td>\r\n            <td style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;"><img style="height:267px; width:400px" alt="" src="http://vuanhhospital.com.vn/Upload/images/IMG_phong%20benh%2001%282%29.jpg" /></span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;">&nbsp;</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;"><strong>PH&Ograve;NG ĐƠN I</strong><br />\r\nGi&aacute; ph&ograve;ng l&agrave; 1.900.000 đồng<br />\r\n</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12px;"><span style="font-family: Arial;"><strong>PH&Ograve;NG HAI GIƯỜNG</strong><br />\r\n<em>Gi&aacute; ph&ograve;ng l&agrave; 1.000.000 đồng/1 giường</em><br />\r\nVấn  đề dinh dưỡng cũng l&agrave; yếu tố rất được quan t&acirc;m tại Vũ Anh. C&aacute;c đầu bếp,  điều dưỡng vi&ecirc;n sẽ phục vụ bệnh nh&acirc;n, th&acirc;n nh&acirc;n 24/24 giờ theo thực đơn  cho từng trường hợp bệnh l&yacute; v&agrave; sở th&iacute;ch của bệnh nh&acirc;n. Bữa ăn được nh&acirc;n  vi&ecirc;n phục vụ tận ph&ograve;ng bệnh nh&acirc;n.</span></span></p>\r\n<p>&nbsp;</p>', 'bang-gia-phong-benh-noi-tru-1461121468.jpg', 3, 'bang-gia-phong-benh-noi-tru', '0');
INSERT INTO `general_settings` (`id`, `name`, `description`, `keywords`, `currency`, `currency_code`, `email`, `theme`, `review_type`, `update_date`) VALUES
(1, 'Ndotdeals | Groupon clone Application | Open source Groupon clone Application', 'Ndotdeals provides deals offer across world.Ndotdeal is groupon cloned application.Its fully free software.', 'ndotdeals,groupon clone,open source groupon clone,groupon,deals application,coupon application', '$', 'USD', 'demo@ndot.in', 'blue', 2, '2011-02-09 01:46:53');
INSERT INTO `goods` (`Id`, `title_vn`, `description_vn`, `content_vn`, `alias`, `date`, `hoten`, `email`, `id_user`, `idcat`, `sort`, `ticlock`) VALUES
(1, 'Bệnh viện có tiếp nhận điều trị bệnh nhân ung thư giai đoạn cuối', 'Mẹ tôi bị ung thư đại trực tràng giai đoạn cuối, đã phẫu thuật hậu môn nhân tạo (HMNT). Hiện đang điều trị, chăm sóc giảm nhẹ tại BV. Triều An. Chúng tôi muốn đưa mẹ tôi về BV gần nhà hơn để tiện đi lại chăm sóc. Vậy BV Hoàn Mỹ có nhận bệnh nhân như mẹ tôi không? Nếu có, chúng tôi có cần mang hồ sơ bệnh án đến làm việc trực tiếp với BV không? Xin cảm ơn.', 'Ch&agrave;o c&ocirc; Phong! Bệnh viện Ho&agrave;n Mỹ S&agrave;i G&ograve;n c&oacute; điều trị giảm nhẹ cho c&aacute;c bệnh  nh&acirc;n bị ung thư giai đoạn cuối. Khi cần nhập viện, c&ocirc; đưa người bệnh  c&ugrave;ng bệnh &aacute;n (nếu c&oacute;) đến sảnh nhận bệnh hoặc Ph&ograve;ng Cấp cứu tại tầng  trệt để l&agrave;m thủ tục nhập viện.', NULL, '2016-05-12 10:17:32', 'Trần Hương Phong', 'vucongthanh92@gmail.com', 0, 1, 0, '0'),
(2, 'Viêm gan siêu vi B', 'Năm nay 18 tuổi khám bệnh định kì cho thấy em bị viêm gan siêu vi B, khám chuyên khoa kết quả HBsAg và HBeAg đều dương tính, anti HCV âm tính, cho em hỏi nếu virut không phá hoại gan thì em có hết bệnh hoàn toàn không hay là kéo theo suốt đời', '<p><span style="font-size: 13px;">Ch&agrave;o bạn,&nbsp;</span></p>\r\n<p>Với kết quả:&nbsp;</p>\r\n<ul style="list-style-type: disc;">\r\n    <li><span style="font-size: 13px;">Anti HCV (-): &nbsp;Kh&ocirc;ng nhiễm si&ecirc;u vi gan C</span></li>\r\n    <li>HBsAg (+) v&agrave; HBeAg(+): Nhiễm si&ecirc;u si gan B v&agrave; si&ecirc;u vi B đang hoạt động sinh sản.&nbsp;</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>Dựa v&agrave;o kết quả tr&ecirc;n kh&ocirc;ng đủ để kết luận l&agrave; nhiễm si&ecirc;u vi gan B l&agrave;  cấp hay mạn. Nếu l&agrave; cấp t&iacute;nh th&igrave; tỉ lệ khỏi bệnh l&agrave; 95%. Nếu l&agrave; mạn t&iacute;nh  th&igrave; tỉ lệ khỏi bệnh chỉ 3%,&nbsp;<span style="font-size: 13px;">c&ograve;n lại l&agrave; mang si&ecirc;u vi B suốt đời.</span></p>\r\n<p><span style="font-size: 13px;">Trường hợp mạn t&iacute;nh th&igrave; c&oacute; 30% vi&ecirc;m  gan B mạn cần điều trị thuốc kh&aacute;ng si&ecirc;u vi (điều trị đặc hiệu) để đưa về  dạng ổn định chứ đa số kh&ocirc;ng hết bệnh, phải kiểm tra theo d&otilde;i định k&igrave;  để ph&aacute;t hiện những trường hợp cần điều trị hay những biến chứng của  bệnh.&nbsp;</span></p>\r\n<p><span style="font-size: 13px;">Trường hợp của bạn nếu men gan b&igrave;nh thường (virus kh&ocirc;ng ph&aacute; hoại gan) th&igrave; đa số l&agrave; mạn t&iacute;nh.</span></p>', NULL, '2016-05-12 10:28:41', 'Vũ Công Thành', 'vucongthanh92@gmail.com', 0, 1, 0, '0'),
(3, 'Viêm gan B có nên ăn trứng?', 'Chế độ sống hằng ngày (ăn uống, làm việc, ngủ nghỉ,...) của người viêm gan B thì phải tuyệt đối kiêng cữ cái gì, cái gì hạn chế ăn, cái gì nên ăn nhiều? Tôi tìm hiểu trên một số website trên mạng có nói là không được ăn trứng, có trang thì nói ăn được, mong bác sĩ giải đáp giúp vấn đề này. Việc thức khuya có ảnh hưởng nhiều đến viêm gan B không ạ? Viêm gan B có lây qua cho vợ, con cái không?', '<div style="text-align: justify;">Người bị Vi&ecirc;m gan si&ecirc;u vi B nếu ở giai  đoạn ổn định th&igrave; chế độ ăn như người b&igrave;nh thường, tức l&agrave; cần ăn đa dạng  v&agrave; c&acirc;n đối đủ 4 nh&oacute;m chất dinh dưỡng: đường (cơm, m&igrave;, bắp, khoai&hellip;),  b&eacute;o, đạm, kho&aacute;ng chất: rau củ quả. Kh&ocirc;ng n&ecirc;n ăn qu&aacute; nhiều thứ n&agrave;y m&agrave;  thiếu thứ kia.</div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: justify;">Nếu chưa bị xơ gan hoặc kh&ocirc;ng bị vi&ecirc;m  gan cấp t&iacute;nh th&igrave; mỗi tuần c&oacute; thể ăn được 3 trứng g&agrave; vịt, nếu đ&atilde; bị xơ  gan c&oacute; thể chỉ n&ecirc;n ăn l&ograve;ng trắng v&agrave; hạn chế l&ograve;ng đỏ; bạn ch&uacute; &yacute; phải  ki&ecirc;ng rượu bia. Nếu chưa xơ gan hoặc kh&ocirc;ng c&oacute; vi&ecirc;m gan cấp th&igrave; l&agrave;m việc  b&igrave;nh thường, hạn chế thức khuya hoặc thiếu ngủ (b&igrave;nh thường ngủ đủ l&agrave;  7-8 giờ/ng&agrave;y).</div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: justify;">Vi&ecirc;m gan B c&oacute; l&acirc;y qua cho vợ, con c&aacute;i  kh&ocirc;ng? T&ocirc;i xin trả lời l&agrave; c&oacute; thể l&acirc;y. Si&ecirc;u vi vi&ecirc;m gan B c&oacute; thể l&acirc;y  truyền từ người n&agrave;y qua người kh&aacute;c qua 3 con đường ch&iacute;nh: tiếp x&uacute;c m&aacute;u,  t&igrave;nh dục, từ mẹ l&acirc;y sang con.</div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: justify;">L&acirc;y qua đường m&aacute;u khi d&ugrave;ng kim ti&ecirc;m  chung khi ch&iacute;ch thuốc, truyền m&aacute;u, l&acirc;y qua vết thương, l&acirc;y khi xăm h&igrave;nh  nếu d&ugrave;ng dụng cụ chung, d&ugrave;ng đồ cắt m&oacute;ng tay, cạo r&acirc;u, b&agrave;n chải đ&aacute;nh  răng chung. Để tr&aacute;nh bị l&acirc;y nhiễm qua con đường m&aacute;u, bạn n&ecirc;n cẩn thận,  kh&ocirc;ng được sử dụng chung dụng cụ c&aacute; nh&acirc;n.</div>\r\n<div style="text-align: justify;">&ograve;n l&acirc;y qua t&igrave;nh dục, tốt nhất l&agrave; bạn n&ecirc;n đưa vợ đi x&eacute;t nghiệm, ch&iacute;ch ngừa vi&ecirc;m gan B. </div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: justify;">Người mẹ c&oacute; thể l&acirc;y truyền si&ecirc;u vi  vi&ecirc;m gan B cho con, d&ugrave; sinh thường hay sinh mổ. V&igrave; thế, để giảm nguy cơ  l&acirc;y nhiễm cho trẻ từ mẹ, trước l&uacute;c vợ chuẩn bị sinh, bạn n&ecirc;n b&aacute;o trước  cho B&aacute;c sĩ khoa sản để B&aacute;c sĩ đưa b&eacute; đi ch&iacute;ch ngừa kh&aacute;ng thể HBIG trong  v&ograve;ng 12 giờ đầu sau sinh.</div>\r\n<p><strong><br />\r\n</strong> <strong>B&aacute;c sĩ L&Ecirc; VĂN CH&Acirc;U</strong></p>', NULL, '2016-05-12 10:32:48', 'Vũ Công Thành', 'vucongthanh92@gmail.com', 0, 1, 0, '0'),
(4, 'Cách nhận biết trẻ suy dinh dưỡng', 'Con em 17 tháng tuổi chỉ nặng 10 kg, có bị suy dinh dưỡng không, cần bổ sung thuốc gì cho bé ăn ngon và tăng cân nhanh', '<div style="text-align: justify;">Để biết b&eacute; c&oacute; suy dinh dưỡng hay  kh&ocirc;ng, bạn n&ecirc;n theo d&otilde;i c&acirc;n nặng theo biểu đồ tăng trưởng suốt từ l&uacute;c  sinh đến số th&aacute;ng tuổi hiện tại của con bạn. Trong bất kỳ sổ kh&aacute;m sức  khỏe n&agrave;o của b&eacute; cũng đều c&oacute; biểu đồ tăng trưởng n&agrave;y.</div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: justify;">Bằng c&aacute;ch chiếu tr&ecirc;n biểu đồ tăng  trưởng c&oacute; trục dọc l&agrave; c&acirc;n nặng theo kg, trục ngang l&agrave; tuổi t&iacute;nh theo  th&aacute;ng, nh&igrave;n v&agrave;o điểm giao nhau giữa hai trục tr&ecirc;n th&igrave; ch&uacute;ng t&ocirc;i thấy con  bạn vẫn nằm trong k&ecirc;nh sức khỏe b&igrave;nh thường. Từ đ&oacute; suy ra con bạn 17  th&aacute;ng tuổi nặng 10 kg l&agrave; b&igrave;nh thường.</div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: justify;">Về chế độ dinh dưỡng, bạn n&ecirc;n cho b&eacute;  ăn uống đầy đủ chất, sao cho c&acirc;n bằng giữa c&aacute;c nh&oacute;m chất. Ở lứa tuổi n&agrave;y  b&eacute; n&ecirc;n được ăn 3 bữa ch&aacute;o v&agrave; 2 bữa sữa mỗi ng&agrave;y. Lượng thực phẩm cần  thiết trong một ng&agrave;y của b&eacute; như sau:</div>\r\n<p><br />\r\n- Gạo: 100-150 g.<br />\r\n- Đạm (thịt, c&aacute;, t&ocirc;m): 100-120 g.<br />\r\n- Dầu: 20-30 g.<br />\r\n- Rau xanh: 50-60 g.<br />\r\n- Tr&aacute;i c&acirc;y: 100-150 g.<br />\r\n- Sữa: 500 ml (nếu kh&ocirc;ng b&uacute; mẹ).<br />\r\nCh&uacute;c bạn v&agrave; ch&aacute;u lu&ocirc;n mạnh khỏe.<br />\r\n<br />\r\n<strong>B&aacute;c sĩ Phạm Thị Mỹ Dung</strong></p>', 'cach-nhan-biet-tre-suy-dinh-duong', NULL, 'Bác sĩ Phạm Thị Mỹ Dung', 'vucongthanh92@gmail.com', 0, 2, 1, '0'),
(5, 'Đầy bụng khó chịu sau khi ngủ trưa', 'Tôi thường ăn trưa sau 30 phút mới đi ngủ và ít khi ngủ được. Khi dậy thường thấy đầy bụng, khó chịu, phải 30 phút sau mới hết. Xin hỏi bác sĩ hiện tượng này là vì sao và cách chữa thế nào?', '<p>Ch&agrave;o bạn,</p>\r\n<div style="text-align: justify;">V&igrave; trong thư bạn kh&ocirc;ng n&ecirc;u r&otilde; l&agrave; nam  hay nữ, bao nhi&ecirc;u tuổi, bệnh bao l&acirc;u, c&oacute; sốt, đau bụng, sụt c&acirc;n hay  kh&ocirc;ng, t&igrave;nh trạng đi ti&ecirc;u thế n&agrave;o&hellip; Với những triệu chứng m&ocirc; tả ở tr&ecirc;n,  t&ocirc;i nghĩ t&igrave;nh trạng của bạn li&ecirc;n quan đến bệnh dạ d&agrave;y.</div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: justify;">Nếu bạn bị đầy bụng, kh&oacute; ti&ecirc;u m&agrave; k&egrave;m  theo c&aacute;c triệu chứng kh&aacute;c như nước tiểu bị sậm m&agrave;u, ph&acirc;n x&aacute;m, v&agrave;ng hoặc  bạc m&agrave;u, n&ocirc;n &oacute;i, ch&aacute;n ăn, mệt mỏi, suy nhược cơ thể&hellip; Đ&acirc;y c&oacute; thể l&agrave; triệu  chứng bệnh l&yacute; của gan.</div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: justify;">Ngo&agrave;i ra cũng c&oacute; một số yếu tố kh&aacute;c c&oacute;  thể l&agrave;m tăng nguy cơ n&agrave;y như t&aacute;c hại của việc h&uacute;t thuốc, rượu bia, tinh  thần căng thẳng, mệt mỏi&hellip;, hay trước khi đi ngủ m&agrave; ăn qu&aacute; nhiều chất  b&eacute;o, ngọt cũng c&oacute; thể dẫn đến đầy bụng, kh&oacute; ti&ecirc;u.</div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: justify;">Bạn n&ecirc;n hạn chế ăn mặn, chua, hạn chế  uống bia, rượu, c&aacute;c nước uống c&oacute; ga&hellip; V&agrave; n&ecirc;n thường xuy&ecirc;n bổ sung men  ti&ecirc;u h&oacute;a (men ti&ecirc;u h&oacute;a c&oacute; trong sữa chua, hay trong thực phẩm tự nhi&ecirc;n  như mầm gi&aacute;), ăn thực phẩm nhiều chất xơ như rau xanh v&agrave; củ quả.</div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: justify;">Tốt nhất bạn n&ecirc;n đi kh&aacute;m chuy&ecirc;n khoa  ti&ecirc;u h&oacute;a &ndash; gan mật tại c&aacute;c bệnh viện để được b&aacute;c sĩ chuy&ecirc;n khoa tư vấn,  điều trị. Nếu cần tư vấn th&ecirc;m, bạn c&oacute; thể gọi điện thoại trực tiếp số  0909 602 827 v&agrave;o c&aacute;c buổi chiều để được hỗ trợ.</div>\r\n<p><br />\r\n<strong>Thạc sĩ, b&aacute;c sĩ B&ugrave;i Quang Đi<br />\r\n</strong></p>', 'day-bung-kho-chiu-sau-khi-ngu-trua', NULL, 'Vũ Công Thành', 'Thạc sĩ, bác sĩ Bùi Quang Đi', 0, 2, 1, '0');
