INSERT INTO `partner` (`Id`, `title_vn`, `title_en`, `description_vn`, `description_en`, `content_vn`, `content_en`, `images`, `sort`, `date`, `ticlock`) VALUES
(1, 'Khách hàng Lê Thị Thu Vân', 'Khách hàng Lê Thị Thu Vân', 'Sự chăm sóc của bác sĩ đã mang cho tôi một niềm tin, một nghị lực để vượt qua bệnh tật. Đội ngũ các em điều dưỡng cũng rất ân cần gần gũi làm cho tôi cảm thấy không khí trong bệnh viện như ở trong gia đình.', 'Sự chăm sóc của bác sĩ đã mang cho tôi một niềm tin, một nghị lực để vượt qua bệnh tật. Đội ngũ các em điều dưỡng cũng rất ân cần gần gũi làm cho tôi cảm thấy không khí trong bệnh viện như ở trong gia đình.', '<h1 style="text-align: center;">Thư Cảm Ơn</h1>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size: 14px;">K&iacute;nh gửi Gi&aacute;m đốc Bệnh viện Quốc tế Vũ Anh!</span></p>\r\n<p><span style="font-size: 14px;">&nbsp;</span></p>\r\n<p><span style="font-size: 14px;">T&ocirc;i t&ecirc;n l&agrave; L&ecirc; Thị Thu V&acirc;n hiện đang l&agrave; bệnh nh&acirc;n tại ph&ograve;ng 109 khoa Nội. Trong qu&aacute; tr&igrave;nh l&agrave;m việc, t&ocirc;i đ&atilde; mắc phải chứng bệnh tai biến, v&agrave; n&oacute; cứ t&aacute;i đi t&aacute;i lại nhiều lần. Nhưng lần n&agrave;o cũng được c&aacute;c b&aacute;c sĩ, đặc biệt l&agrave; b&aacute;c sĩ Trần Nguyệt &Aacute;nh đ&atilde; tận t&igrave;nh cứu chữa. Sự chăm s&oacute;c của b&aacute;c sĩ đ&atilde; mang cho t&ocirc;i một niềm tin, một nghị lực để vượt qua bệnh tật. Đội ngũ c&aacute;c em điều dưỡng cũng rất &acirc;n cần gần gũi l&agrave;m cho t&ocirc;i cảm thấy kh&ocirc;ng kh&iacute; trong bệnh viện như ở trong gia đ&igrave;nh.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;">&nbsp;</span></p>\r\n<p><span style="font-size: 14px;">T&ocirc;i viết thư n&agrave;y gửi đến gi&aacute;m đốc, tập thể c&aacute;c y b&aacute;c sĩ, b&aacute;c sĩ &Aacute;nh, đội ngũ điều dưỡng lời c&aacute;m ơn ch&acirc;n th&agrave;nh của t&ocirc;i. Xin k&iacute;nh ch&uacute;c gi&aacute;m đốc, tập thể c&aacute;c y b&aacute;c sĩ, b&aacute;c sĩ &Aacute;nh, đội ngũ điều dưỡng lu&ocirc;n lu&ocirc;n mạnh khỏe, ho&agrave;n th&agrave;nh xuất sắc c&ocirc;ng việc của m&igrave;nh, cứu chữa được nhiều người bệnh, lu&ocirc;n may mắn v&agrave; hạnh ph&uacute;c trong cuộc sống.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;">&nbsp;</span></p>\r\n<p><span style="font-size: 14px;">Kh&aacute;ch h&agrave;ng,</span><span style="font-size: 14px;"><br />\r\n</span></p>\r\n<p><span style="font-size: 14px;"><br type="_moz" />\r\n</span></p>\r\n<p><span style="font-size: 14px;">L&ecirc; Thị Thu V&acirc;n</span></p>', '<h1 style="text-align: center;">Thư Cảm Ơn</h1>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size: 14px;">K&iacute;nh gửi Gi&aacute;m đốc Bệnh viện Quốc tế Vũ Anh!</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;">&nbsp;</span></p>\r\n<p><span style="font-size: 14px;">T&ocirc;i t&ecirc;n l&agrave; L&ecirc; Thị Thu V&acirc;n hiện đang l&agrave; bệnh nh&acirc;n tại ph&ograve;ng 109 khoa Nội. Trong qu&aacute; tr&igrave;nh l&agrave;m việc, t&ocirc;i đ&atilde; mắc phải chứng bệnh tai biến, v&agrave; n&oacute; cứ t&aacute;i đi t&aacute;i lại nhiều lần. Nhưng lần n&agrave;o cũng được c&aacute;c b&aacute;c sĩ, đặc biệt l&agrave; b&aacute;c sĩ Trần Nguyệt &Aacute;nh đ&atilde; tận t&igrave;nh cứu chữa. Sự chăm s&oacute;c của b&aacute;c sĩ đ&atilde; mang cho t&ocirc;i một niềm tin, một nghị lực để vượt qua bệnh tật. Đội ngũ c&aacute;c em điều dưỡng cũng rất &acirc;n cần gần gũi l&agrave;m cho t&ocirc;i cảm thấy kh&ocirc;ng kh&iacute; trong bệnh viện như ở trong gia đ&igrave;nh.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;">&nbsp;</span></p>\r\n<p><span style="font-size: 14px;">T&ocirc;i viết thư n&agrave;y gửi đến gi&aacute;m đốc, tập thể c&aacute;c y b&aacute;c sĩ, b&aacute;c sĩ &Aacute;nh, đội ngũ điều dưỡng lời c&aacute;m ơn ch&acirc;n th&agrave;nh của t&ocirc;i. Xin k&iacute;nh ch&uacute;c gi&aacute;m đốc, tập thể c&aacute;c y b&aacute;c sĩ, b&aacute;c sĩ &Aacute;nh, đội ngũ điều dưỡng lu&ocirc;n lu&ocirc;n mạnh khỏe, ho&agrave;n th&agrave;nh xuất sắc c&ocirc;ng việc của m&igrave;nh, cứu chữa được nhiều người bệnh, lu&ocirc;n may mắn v&agrave; hạnh ph&uacute;c trong cuộc sống.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;">&nbsp;</span></p>\r\n<p><span style="font-size: 14px;">Kh&aacute;ch h&agrave;ng,</span><span style="font-size: 14px;"><br />\r\n</span></p>\r\n<p><span style="font-size: 14px;"><br type="_moz" />\r\n</span></p>\r\n<p><span style="font-size: 14px;">L&ecirc; Thị Thu V&acirc;n</span></p>', '6ac8eJFTfO.jpg', 4, '2016-03-28 00:00:00', '0'),
(5, 'Đội xe cấp cứu chuyên dụng', 'Đội xe cấp cứu chuyên dụng', 'Đội xe cấp cứu chuyên dụng, cơ động do Mỹ sản xuất, đạt tiêu chuẩn quốc tế, phục vụ 24/24h. trong trường hợp khẩn cấp', 'Đội xe cấp cứu chuyên dụng, cơ động do Mỹ sản xuất, đạt tiêu chuẩn quốc tế, phục vụ 24/24h. trong trường hợp khẩn cấp', '<p>Đội xe cấp cứu chuy&ecirc;n dụng, cơ động do Mỹ sản xuất, đạt ti&ecirc;u chuẩn quốc tế, phục vụ 24/24h. trong trường hợp khẩn cấp</p>', '<p>Đội xe cấp cứu chuy&ecirc;n dụng, cơ động do Mỹ sản xuất, đạt ti&ecirc;u chuẩn quốc tế, phục vụ 24/24h. trong trường hợp khẩn cấp</p>', 'TQxpqC74QH.jpg', 0, '2016-04-19 00:00:00', '0'),
(3, 'Chồng sản phụ Huỳnh Thị Kim Chi', 'bé Đặng Hoài Thương 075 - Mẹ Trần Thị Ly', 'Tôi rất hài lòng và chân thành cảm ơn bác sĩ, các anh chị em nhân viên bệnh viện, đặc biệt khoa Sản đã tận tâm giúp đỡ gia đình được an toàn và hạnh phúc, để lại nhiều kỷ niệm đẹp.', 'Bệnh viện Đa Khoa Vạn Hạnh được xây dựng để đáp ứng nhu cầu sức khoẻ của cộng đồng. Bệnh viện chính thức hoạt động vào ngày 27 tháng 07 năm 2000.', '<h1 style="text-align: center;"><span style="font-size: 14px;">Thư Cảm Ơn</span></h1>\r\n<p><span style="font-size: 14px;"><br />\r\nT&ocirc;i t&ecirc;n Nguyễn Hữu Hải &Acirc;u, chồng sản phụ Huỳnh Thị Kim Chi, sinh con trai ng&agrave;y 27/11/2015. T&ocirc;i rất h&agrave;i l&ograve;ng v&agrave; ch&acirc;n th&agrave;nh cảm ơn b&aacute;c sĩ, c&aacute;c anh chị em nh&acirc;n vi&ecirc;n bệnh viện, đặc biệt khoa Sản đ&atilde; tận t&acirc;m gi&uacute;p đỡ gia đ&igrave;nh được an to&agrave;n v&agrave; hạnh ph&uacute;c, để lại nhiều kỷ niệm đẹp. Tr&acirc;n trọng cảm ơn.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nKh&aacute;ch h&agrave;ng,</span></p>\r\n<p><span style="font-size: 14px;"><br />\r\nNguyễn Hữu Hải &Acirc;u</span></p>\r\n<p><span style="font-size:14.0pt;line-height:115%;font-family:&#10;&quot;Times New Roman&quot;,&quot;serif&quot;"><o:p></o:p></span></p>\r\n<p>&nbsp;</p>', '<h1 style="text-align: center;"><span style="font-size: 14px;">Thư Cảm Ơn</span></h1>\r\n<p><span style="font-size: 14px;">T&ocirc;i t&ecirc;n Nguyễn Hữu Hải &Acirc;u, chồng sản phụ Huỳnh Thị Kim Chi, sinh con trai ng&agrave;y 27/11/2015. T&ocirc;i rất h&agrave;i l&ograve;ng v&agrave; ch&acirc;n th&agrave;nh cảm ơn b&aacute;c sĩ, c&aacute;c anh chị em nh&acirc;n vi&ecirc;n bệnh viện, đặc biệt khoa Sản đ&atilde; tận t&acirc;m gi&uacute;p đỡ gia đ&igrave;nh được an to&agrave;n v&agrave; hạnh ph&uacute;c, để lại nhiều kỷ niệm đẹp. Tr&acirc;n trọng cảm ơn.</span></p>\r\n<p style="font-size: 12px;"><o:p></o:p></p>\r\n<p style="font-size: 12px;"><span style="font-size: 14px;"><br />\r\nKh&aacute;ch h&agrave;ng,</span></p>\r\n<p style="font-size: 12px;"><span style="font-size: 14px;"><br />\r\n</span></p>\r\n<p><span style="font-size: 14px;">Nguyễn Hữu Hải &Acirc;u</span></p>\r\n<p>&nbsp;</p>', 'tXNbUdKIHJ.jpg', 3, '2016-03-28 00:00:00', '0'),
(2, 'Khách hàng Trần Thị Thu Ngân', 'Khách hàng Trần Thị Thu Ngân', 'Xin chân thành cảm ơn đội ngũ y bác sĩ, y tá, điều dưỡng đã cấp cứu tôi ngày 9/12/2014, vượt qua cơn nguy hiểm.', 'Xin chân thành cảm ơn đội ngũ y bác sĩ, y tá, điều dưỡng đã cấp cứu tôi ngày 9/12/2014, vượt qua cơn nguy hiểm.', '<h1 style="text-align: center;"><span style="font-size: 14px;">Thư Cảm Ơn</span></h1>\r\n<p><span style="font-size: 14px;"><br />\r\nXin ch&acirc;n th&agrave;nh cảm ơn đội ngũ y b&aacute;c sĩ, y t&aacute;, điều dưỡng đ&atilde; cấp cứu t&ocirc;i ng&agrave;y 9/12/2014, vượt qua cơn nguy hiểm. </span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nTrong thời gian điều trị, b&aacute;c sĩ, y t&aacute;, hộ l&yacute; đ&atilde; quan t&acirc;m v&agrave; chăm s&oacute;c t&ocirc;i rất tốt. Gia đ&igrave;nh t&ocirc;i thực sự rất h&agrave;i l&ograve;ng.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nKh&aacute;ch h&agrave;ng,</span></p>\r\n<p><span style="font-size: 14px;"><br />\r\nTrần Thị Thu Ng&acirc;n</span></p>\r\n<p><span style="font-size:14.0pt;line-height:115%;font-family:&#10;&quot;Times New Roman&quot;,&quot;serif&quot;"><o:p></o:p></span></p>\r\n<p>&nbsp;</p>', '<h1 style="text-align: center;"><span style="font-size: 14px;">Thư Cảm Ơn</span></h1>\r\n<p><span style="font-size: 14px;"><br />\r\nXin ch&acirc;n th&agrave;nh cảm ơn đội ngũ y b&aacute;c sĩ, y t&aacute;, điều dưỡng đ&atilde; cấp cứu t&ocirc;i ng&agrave;y 9/12/2014, vượt qua cơn nguy hiểm. </span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nTrong thời gian điều trị, b&aacute;c sĩ, y t&aacute;, hộ l&yacute; đ&atilde; quan t&acirc;m v&agrave; chăm s&oacute;c t&ocirc;i rất tốt. Gia đ&igrave;nh t&ocirc;i thực sự rất h&agrave;i l&ograve;ng.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nKh&aacute;ch h&agrave;ng,</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nTrần Thị Thu Ng&acirc;n</span></p>', 'GFW1hZmS2B.jpg', 2, '2016-03-28 00:00:00', '0'),
(4, 'Khách hàng Trần Thị Minh Thủy', 'Khách hàng Trần Thị Minh Thủy', 'Ngày ra viện tôi vẫn nhớ các anh, chị em từ bác sĩ, đến nữ hộ sinh nói riêng, nhân viên phục vụ nói chung đã làm cho tôi luôn nhớ mãi, đã vui vẻ, nhiệt tình phục vụ chu đáo, còn thể hiện trách nhiệm tốt, tình cảm gần gũi chan hòa.', 'Ngày ra viện tôi vẫn nhớ các anh, chị em từ bác sĩ, đến nữ hộ sinh nói riêng, nhân viên phục vụ nói chung đã làm cho tôi luôn nhớ mãi, đã vui vẻ, nhiệt tình phục vụ chu đáo, còn thể hiện trách nhiệm tốt, tình cảm gần gũi chan hòa.', '<h1 style="text-align: center;"><span style="font-size: 14px;">Thư Cảm Ơn</span></h1>\r\n<p><span style="font-size: 14px;"><br />\r\nNg&agrave;y ra viện t&ocirc;i vẫn nhớ c&aacute;c anh, chị em từ b&aacute;c sĩ, đến nữ hộ sinh n&oacute;i ri&ecirc;ng, nh&acirc;n vi&ecirc;n phục vụ n&oacute;i chung đ&atilde; l&agrave;m cho t&ocirc;i lu&ocirc;n nhớ m&atilde;i, đ&atilde; vui vẻ, nhiệt t&igrave;nh phục vụ chu đ&aacute;o, c&ograve;n thể hiện tr&aacute;ch nhiệm tốt, t&igrave;nh cảm gần gũi chan h&ograve;a.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nT&ocirc;i l&agrave; mẹ của ch&aacute;u Phạm Thị Minh Thủy. Thay mặt cho gia đ&igrave;nh cảm ơn c&aacute;c b&aacute;c sĩ v&agrave; anh chị em bệnh viện Vũ Anh, đ&atilde; thể hiện tốt v&agrave; đầy tr&aacute;ch nhiệm.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nT&ocirc;i v&agrave; ch&aacute;u Thủy xin chia tay v&agrave; k&iacute;nh ch&uacute;c c&aacute;c anh, chị l&atilde;nh đạo c&ugrave;ng c&aacute;n bộ nh&acirc;n vi&ecirc;n bệnh viện lu&ocirc;n mạnh khỏe, ph&aacute;t huy những mặt tốt đ&atilde; c&oacute;. </span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nK&iacute;nh ch&agrave;o c&aacute;c anh, chị em bệnh viện Vũ Anh!</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nKh&aacute;ch h&agrave;ng</span></p>\r\n<p><span style="font-size: 14px;"><br />\r\nTrần Thị Minh Thủy</span></p>\r\n<p class="MsoNormal"><span style="font-size:14.0pt;line-height:115%;font-family:&#10;&quot;Times New Roman&quot;,&quot;serif&quot;"><o:p></o:p></span></p>\r\n<p>&nbsp;</p>', '<h1 style="text-align: center;"><span style="font-size: 14px;">Thư Cảm Ơn</span><o:p></o:p></h1>\r\n<p><span style="font-size: 14px;"><br />\r\nNg&agrave;y ra viện t&ocirc;i vẫn nhớ c&aacute;c anh, chị em từ b&aacute;c sĩ, đến nữ hộ sinh n&oacute;i ri&ecirc;ng, nh&acirc;n vi&ecirc;n phục vụ n&oacute;i chung đ&atilde; l&agrave;m cho t&ocirc;i lu&ocirc;n nhớ m&atilde;i, đ&atilde; vui vẻ, nhiệt t&igrave;nh phục vụ chu đ&aacute;o, c&ograve;n thể hiện tr&aacute;ch nhiệm tốt, t&igrave;nh cảm gần gũi chan h&ograve;a.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nT&ocirc;i l&agrave; mẹ của ch&aacute;u Phạm Thị Minh Thủy. Thay mặt cho gia đ&igrave;nh cảm ơn c&aacute;c b&aacute;c sĩ v&agrave; anh chị em bệnh viện Vũ Anh, đ&atilde; thể hiện tốt v&agrave; đầy tr&aacute;ch nhiệm.</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nT&ocirc;i v&agrave; ch&aacute;u Thủy xin chia tay v&agrave; k&iacute;nh ch&uacute;c c&aacute;c anh, chị l&atilde;nh đạo c&ugrave;ng c&aacute;n bộ nh&acirc;n vi&ecirc;n bệnh viện lu&ocirc;n mạnh khỏe, ph&aacute;t huy những mặt tốt đ&atilde; c&oacute;. </span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nK&iacute;nh ch&agrave;o c&aacute;c anh, chị em bệnh viện Vũ Anh!</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nKh&aacute;ch h&agrave;ng</span><o:p></o:p></p>\r\n<p><span style="font-size: 14px;"><br />\r\nTrần Thị Minh Thủy</span><span style="font-size:14.0pt;line-height:115%;font-family:&#10;&quot;Times New Roman&quot;,&quot;serif&quot;"><o:p></o:p></span></p>', 'cAeAnBXqjF.jpg', 1, '2016-03-28 00:00:00', '0');
